import React, { Component } from "react";
import Web3 from "web3";
import Auction from '../contracts/Auction.json'
import NavBar from "./NavBar.js";
import Main from "./Main";


class App extends Component {

  async UNSAFE_componentWillMount(){
    await this.LoadWeb3()
    await this.LoadBLockChain()
  }

  async LoadWeb3(){
    if(window.ethereum)
    { 
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3( window.web3.currentProvider)
    }
    else{
      window.alert('No MetaMask or anyother connection Detected')
    }

  }
  async LoadBLockChain(){
    const web3 = window.web3
    const account = await web3.eth.getAccounts()
    this.setState({account:account[0]})
    console.log(account)

    //Ganache Network ID
    const networkID = await web3.eth.net.getId()

    //Load Contract
    const ContractData = Auction.networks[networkID]
    if(ContractData){
      const cont = new web3.eth.Contract(Auction.abi,ContractData.address)
      this.setState({auction:cont})  

      const owner_addr = await cont.methods.GetOwner().call()
      this.setState({auction_owner:owner_addr})
      console.log("Onwer Address: ", owner_addr)

      const  a = cont.methods.GetHighestBid().call()
      console.log("Asa: ",a[1])
    }
    this.setState({loading:false})


  }
  PlaceBid = (amount) =>{
    this.setState({loading:true})
    this.state.auction.methods.BidNow(amount).send({from:this.state.account}).on('transactionHash',(hash)=>{
      this.setState({loading:false})
      this.DisplayRes()
    })
  }

  DisplayRes = () =>{

       let prev = this.state.auction.methods.GetPrevBid()
      console.log("Prev Addr : " , prev[0] , "Prev Amount: " , prev[1])

      let curr = this.state.auction.methods.GetHighestBid()
      console.log("Curr Addr : " , curr[0] , "Curr Amount: " , curr[1])

  }
  constructor(props){
    super(props)
      this.state={
        auction_owner:'0x0',
        account:'0x00001',
        auction:{},
        loading:true
      }

  }

  render(){
    let content
    {this.state.loading? content = <p id='loader' className='text-center' style={{marginTop:'100px',color:'red'}}><strong>Loading Contracts...</strong></p>
    :
    content = <Main
    PlaceBid = {this.PlaceBid}
    Result = {this.DisplayRes}

    />
    }
    return(
      <div>

      <div>
      <NavBar account = {this.state.account} owner ={this.state.auction_owner} />
      </div>
      {content}
      <div>

      </div>

      </div>
    )
  }
}
export default App;
