import React, { Component } from "react";
import Web3 from "web3";
import Auction from '../contracts/Auction.json'
import NavBar from "./NavBar.js";


class App extends Component {

  async UNSAFE_componentWillMount(){
    await this.LoadWeb3()
    await this.LoadBLockChain()
  }

  async LoadWeb3(){
    if(window.ethereum)
    { 
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3( window.web3.currentProvider)
    }
    else{
      window.alert('No MetaMask or anyother connection Detected')
    }

  }
  async LoadBLockChain(){
    const web3 = window.web3
    const account = await web3.eth.getAccounts()
    this.setState({account:account[0]})
    console.log(account)

    //Ganache Network ID
    const networkID = await web3.eth.net.getId()

    //Load Contract
    const ContractData = Auction.networks[networkID]
    if(ContractData){
      const cont = new web3.eth.Contract(Auction.abi,ContractData.address)
      this.setState({auction:cont})  

      const owner_addr = await cont.methods.GetOwner().call()
      this.setState({auction_owner:owner_addr})
      console.log("Onwer Address: ", owner_addr)

      const test = await cont.methods.GetHighestBid().call()
      console.log("Result: ",test[0])
    }


  }
  constructor(props){
    super(props)
      this.state={
        auction_owner:'0x0',
        account:'0x00001',
        auction:{}
      }

  }

  render(){
    return(

      <NavBar account = {this.state.account} owner ={this.state.auction_owner} />

    )
  }
}
export default App;
