import React, { Component } from "react";
import Web3 from "web3";
import storage_contract from './contracts/SimpleStorage.json'

import "./App.css";

class App extends Component {

  async UNSAFE_componentWillMount(){
    await this.LoadWeb3()
    await this.LoadBLockChain()
  }

  async LoadWeb3(){
    if(window.ethereum)
    { 
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if(window.web3)
    {
      window.web3 = new Web3( window.web3.currentProvider)
    }
    else{
      window.alert('No MetaMask or anyother connection Detected')
    }

  }
  async LoadBLockChain(){
    const web3 = window.web3
    const account = await web3.eth.getAccounts()
    this.setState({account:account[0]})
    console.log(account)

    //Ganache Network ID
    const networkID = await web3.eth.net.getId()

    //Load Contract
    const ContractData = storage_contract.networks[networkID]
    if(ContractData){
      const cont = new web3.eth.Contract(storage_contract.abi,ContractData.address)
      this.setState({storage_contract:cont})  
    }


  }
  constructor(props){
    super(props)
      this.state={
        account:'0x00001',
        storage_contract:{}
      }

  }

  render(){
    return(
      <div>
        <h1>TEST DAPP Account: {this.state.account}</h1>
      </div>
    )
  }
}
export default App;
